function Jobs_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('Jobs');
	if(!$arrPerm['insert']) return false;

	$data = [
		'client' => Request::lookup('client', ''),
		'software' => Request::lookup('software', ''),
		'endpoint' => Request::lookup('endpoint', ''),
		'method' => Request::lookup('method', ''),
		'destination' => Request::lookup('destination', ''),
	];


	// hook: Jobs_before_insert
	if(function_exists('Jobs_before_insert')) {
		$args = [];
		if(!Jobs_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('Jobs', backtick_keys_once($data), $error);
	if($error) {
		$error_message = $error;
		return false;
	}

	$recID = db_insert_id(db_link());

	update_calc_fields('Jobs', $recID, calculated_fields()['Jobs']);

	// hook: Jobs_after_insert
	if(function_exists('Jobs_after_insert')) {
		$res = sql("SELECT * FROM `Jobs` WHERE `id`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args = [];
		if(!Jobs_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('Jobs', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(strlen(Request::val('SelectedID'))) Jobs_copy_children($recID, Request::val('SelectedID'));

	return $recID;
}

