	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('Log', backtick_keys_once($data), $error);
	if($error) {
		$error_message = $error;
		return false;
	}

	$recID = db_insert_id(db_link());

	update_calc_fields('Log', $recID, calculated_fields()['Log']);

	// hook: Log_after_insert
	if(function_exists('Log_after_insert')) {
