	$data = [
		'client' => Request::lookup('client', ''),
		'endpoint' => Request::lookup('endpoint', ''),
		'job' => Request::lookup('job', ''),
		'status' => Request::val('status', ''),
		'results' => br2nl(Request::val('results', '')),
		'screenshot' => Request::fileUpload('screenshot', [
			'maxSize' => 3072000,
			'types' => 'jpg|jpeg|gif|png|webp',
			'noRename' => false,
			'dir' => '',
			'id' => $selected_id,
			'success' => function($name, $selected_id) {
				createThumbnail($name, getThumbnailSpecs('Log', 'screenshot', 'tv'));
			},
			'removeOnRequest' => true,
			'remove' => function($selected_id) {
				// do nothing: preserve removed files on server.
			},
			'failure' => function($selected_id, $fileRemoved) {
				if($fileRemoved) return '';
				return existing_value('Log', 'screenshot', $selected_id);
			},
		]),
		'notes' => Request::val('notes', ''),
	];

	// get existing values
	$old_data = getRecord('Log', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: Log_before_update
	if(function_exists('Log_before_update')) {
		$args = ['old_data' => $old_data];
		if(!Log_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'Log', 
		backtick_keys_once($set), 
		['`id`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="Log_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('Log', $data['selectedID'], calculated_fields()['Log']);

	// hook: Log_after_update
	if(function_exists('Log_after_update')) {
		$res = sql("SELECT * FROM `Log` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['id'];
		$args = ['old_data' => $old_data];
		if(!Log_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='Log' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

